var span=document.getElementById("btn").getElementsByTagName("span");var ul=document.getElementsByTagName("ul")[0];var li=ul.getElementsByTagName("li");var liWidth=0;var curIndex=0;init();var particle=new Particleground.particle("#particle .container",{opacity:0.7,color:["#66CCCC","#CCFFFF","#FF99CC","#CC9933"],num:0.1,speed:1,max:2.4,min:0.6,dis:150,lineWidth:0.2,r:270,resize:true});var wave,snow;for(var i=0;i<span.length;i++){span[i].index=i;span[i].onclick=function(){curIndex=this.index;slideTab()}}window.onresize=function(){init();slideTab()};document.onkeyup=function(e){switch(e.keyCode){case 37:curIndex--;if(curIndex<0){curIndex=li.length-1}slideTab();break;case 39:curIndex++;if(curIndex>li.length-1){curIndex=0}slideTab();break}};function init(){liWidth=document.documentElement.clientWidth;ul.style.width=liWidth*li.length+"px";for(var i=0;i<li.length;i++){li[i].style.width=liWidth+"px"}}function slideTab(){var translate="translate("+-liWidth*curIndex+"px,0)";ul.style.transform=translate;ul.style.webkitTransform=translate;ul.style.mozTransform=translate;ul.style.msTransform=translate;document.querySelector("#btn .active").removeAttribute("class");span[curIndex].className="active";switch(curIndex){case 0:particle.open();wave&&wave.pause();snow&&snow.pause();break}};